              <Command.Group heading="Results">
                {results.map(result => (
                  <Command.Item
                    key={`${result.type}:${result.id}`}
                    onSelect={() => onSelect(`${result.type}:${result.id}`)}
                  >
                    <div className="flex items-center">
                      {result.type === 'patient' && <User className="h-4 w-4 mr-2" />}
